import React, { useEffect, useState } from "react";
import { Button } from "../../../Button";
import { useHistory, useParams } from "react-router-dom";
import { updateCard } from "../../../../utils/api";

function EditCard({ currentDeck }) {
  const { cardId } = useParams();
  const [editCardData, setEditCardData] = useState({});
  const history = useHistory();
  const currentCard = currentDeck.cards.find(({ id }) => cardId === card.id);
  const { id, front, back, deckId } = currentCard;
  const initialEditCardData = {
    id,
    front,
    back,
    deckId,
  };

  useEffect(() => {
    setEditCardData(initialEditCardData);
  }, []);

  const handleChange = ({ target }) => {
    setEditCardData({
      ...editCardData,
      [target.name]: target.value,
    });
  };

  async function handleSubmit(event) {
    event.preventDefault();
    const abortController = new AbortController();
    await updateCard(editCardData, abortController.signal);
    history.push(`/decks/${deckId}`)
  }

  return (
    <div>
      <h2>Edit Card</h2>
      <form className="form-group">
        <label htmlFor="frontText">Front</label>
        <textarea
          className="form-control"
          id="frontText"
          name="frontText"
          onChange={handleChange}
          value={editCardData.front}
          rows="3"
        />
        <br />
        <label htmlFor="backText">Back</label>
        <textarea
          className="form-control"
          id="backText"
          name="backText"
          onChange={handleChange}
          value={editCardData.back}
          rows="3"
        />
        <Button>Done</Button>
        <Button>Save</Button>
      </form>
    </div>
  );
}

export default EditCard;
