import React, { useEffect, useState } from "react";
import { useParams, useHistory, useRouteMatch } from "react-router-dom";
import { createCard, updateCard } from "../utils/api";
import { Button } from "./Button";

/**
 *
 * @param {object} initialCardData
 * the data to populate the form initially,
 * blank for AddCard, the current card for EditCard
 */

function CardForm({ initialCardData, setLoading, handleChange, handleSubmit }) {
  const { deckId } = useParams();
  const { url } = useRouteMatch();
  const [cardData, setCardData] = useState({});
  const history = useHistory();
  console.log(initialCardData);
  useEffect(() => {
    const abortController = new AbortController();
    async function loadCardData() {
      try {
        setCardData(initialCardData);
      } catch (error) {
        if (error.name === "AbortError") {
          console.log("loadCardData Aborted");
        } else {
          throw error;
        }
      }
    }
    loadCardData();
    return () => abortController.abort();
  }, [url]);

  // const handleChange = ({ target }) => {
  //   setCardData({
  //     ...cardData,
  //     [target.name]: target.value,
  //   });
  // };

  // async function handleSubmit(event) {
  //   event.preventDefault();
  //   const abortController = new AbortController();
  //   if (cardData.cardId) {
  //     await updateCard(cardData, abortController.signal);
  //     history.push(`/decks/${deckId}`);
  //   } else {
  //     await createCard(deckId, cardData, abortController.signal);
  //     setCardData(initialCardData);
  //   }
  //   setLoading(true);
  //   return () => abortController.abort();
  // }

  const renderView = (
    <>
      <form onSubmit={handleSubmit} className="form-group">
        <label htmlFor="front">Front</label>
        <textarea
          className="form-control"
          id="front"
          name="front"
          rows="3"
          onChange={handleChange}
          value={cardData.front}
        />
        <label htmlFor="backText">Back</label>
        <textarea
          className="form-control"
          id="back"
          name="back"
          rows="3"
          onChange={handleChange}
          value={cardData.back}
        />
        <Button
          onClick={() => {
            setLoading(true);
            return history.push(`/decks/${deckId}`);
          }}
        >
          Done
        </Button>
        <Button type="submit">Save</Button>
      </form>
    </>
  );
  return renderView;
}
export default CardForm;
