import React from "react";
import { Route, Switch, Link } from "react-router-dom";
import Button from "./Button";
import CreateDeck from "./CreateDeck";
import Deck from "./Deck";
import DeckThumbnail from "./DeckThumbnail";
import Study from "./Study";


function Home() {
  // TODO: dynamically assign deckId
  const deckId = 1;
  return <div>
    {/* TODO: create deck button takes user to CreateDeck */}
    <Link to="/decks/new"><Button>Create Deck</Button></Link>
    {/* TODO: display existing decks: deck name, number of cards */}
    {/* TODO: buttons: study -> Study, edit-> EditDeck, delete w/ warning */}
    <DeckThumbnail />
    <Switch>
      <Route path={`/decks/${deckId}`}>
        <Deck />
      </Route>
      <Route>
        <Study />
      </Route>
      <Route path="/decks/new">
        <CreateDeck />
      </Route>
    </Switch>
    
    </div>;
}

export default Home;
