import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import routes from "./routes";

const Breadcrumb = ({
  crumbs = [],
  deckId = null,
  cardId = null,
  setRenderFlag,
  renderFlag,
}) => {
  // i.e. crumbs = [{name: "Home", path: "/"}, {name: "Deck", path: `/decks/${deckId}`},{name: "Study"} ]
  const [breadcrumbs, setBreadcrumbs] = useState([]);

  useEffect(() => {
    console.log(crumbs)
    try {
      const crumbArray = crumbs.map(({ crumb }, key) => {
        const found = routes.find((route) => route.name === crumb.name);
        if (found.path.includes(":deckId")) {
          crumb.path.replace(":deckId", deckId);
        }

        if (crumbs.indexOf(crumb) === crumbs.length - 1) {
          return <span>{crumb.name}</span>;
        } else {
          return <Link to={crumb.path}>{crumb.name}</Link>;
        }
      });
      setBreadcrumbs(crumbArray);
    } catch (error) {
      console.log(error);
    }
  }, [renderFlag]);

  return <div>
    <p>there should be a breadcrumbs here</p>
    {breadcrumbs}
    </div>;
};

export default Breadcrumb;
