import React from "react";
import { Link } from "react-router-dom";
import Button from "./Button";
import Deck from "./Deck";
import Study from "./Study";

function DeckThumbnail() {
  // TODO: get deckId dynamically assigned
  const deckId = 1;
  return (
  <div> 
    <div class="card">
      <div class="card-body">
        {/* TODO: deck title here */}
        <h4 class="card-title">Deck title</h4>
        {/* TODO: number of cards here */}
        <h6 class="card-subtitle mb-2 text-muted">Card subtitle</h6>
        <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
        {/* TODO: onClick to go to Study */}
        <Link to="/decks/:deckId/study"><Button>Study</Button></Link>
        {/* TODO: onClick to go to DeckView */}
        <Link to="/decks/:deckId"><Button>View</Button></Link>
        {/* TODO: modal w/ "OK" or "Cancel" */}
        {/* TODO: onClick delete */}
        <Button>Delete</Button>
      </div>
    </div>
    <Study />
    <Deck />
  </div>
);
}

export default DeckThumbnail;
