import React, { useEffect, useState } from "react";
import { Route, Switch, Link, useRouteMatch } from "react-router-dom";
import { listCards, listDecks } from "../utils/api";
import Button from "./Button";
import CreateDeck from "./decks/CreateDeck";
import Deck from "./decks/Deck";
import DeckThumbnail from "./DeckThumbnail";
import Study from "./decks/Study";




function Home() {
  const [decks, setDecks] = useState([]);
  useEffect(() => {
    Promise.resolve(listDecks()).then((result) => setDecks(result));
  }, []);

  console.log(decks)
  
  // take decks and get info for thumbnails



  return <div>

    <Switch>
      <Route exact path="/">
        {/* TODO: Implement the screen starting here */}
        {/* create deck button takes user to CreateDeck */}
        <Link to="/decks/new"><Button>Create Deck</Button></Link>
        {/* TODO: display existing decks: deck name, number of cards */}
        {/* TODO: buttons: study -> Study, edit-> EditDeck, delete w/ warning */}
        <p></p>
        
        <DeckThumbnail />
      </Route>
      <Route path="/decks/new">
        <CreateDeck />
      </Route>
      <Route path="/decks/:deckId/study">
        <Study />
      </Route>
      <Route path="/decks/:deckId">
        <Deck />
      </Route>

      
    </Switch>
    
    </div>;
}

export default Home;
