import React, { useState } from "react";
import { Button } from "../Button";
import Breadcrumb from "../Breadcrumb";
import { useParams, Link } from "react-router-dom";
import { updateDeck } from "../../utils/api";
// /decks/:deckId/edit

function EditDeck({ setRenderFlag, decks }) {
  const { deckId } = useParams();
  const currentDeck = decks.find((deck) => Number(deck.id) === Number(deckId));
  
  const initialEditDeckData = {
    name: currentDeck.name,
    description: currentDeck.description,
  }
  
  const [editDeckData, setEditDeckData] = useState(initialEditDeckData);

  const handleChange = ({ target }) => {
    setEditDeckData({
      ...editDeckData,
      [target.name]: target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setRenderFlag(true);
    Promise.resolve(updateDeck(currentDeck))
      .catch(console.log);
  };

  return (
    <div>
      <h2>Edit Deck</h2>
      <div className="form-group">
        <label className="col-form-label" for="deckName">
          Name
        </label>
        <input
          type="text"
          className="form-control"
          onChange={handleChange}
          value={editDeckData.name}
          id="deckName"
        />
      </div>
      <div className="form-group">
        <label for="deckDescription">Description</label>
        <textarea
          className="form-control"
          onChange={handleChange}
          value={editDeckData.description}
          id="deckDescription"
          rows="3"
        ></textarea>
        <Link to={`/decks/${deckId}`}>
          <Button>Cancel</Button>
        </Link>
        <Button type="submit" onClick={handleSubmit}>
          Submit
        </Button>
      </div>
    </div>
  );
}

export default EditDeck;
