import React, { useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import routes from "./routes";

const Breadcrumb = ({ crumbs }) => {
  // i.e. crumbs = [{name: "Home", path: "/"}, {name: "Deck", path: `/decks/${deckId}`},{name: "Study"} ]

  const crumbArray = crumbs.map(({ crumb }, key) => {
    const found = routes.find((route) => {
      console.log(`${route.name} === ${crumb}`);

      return route.name === crumb;
    });
    if (found.path.includes(":deckId")) {
      const { deckId } = useParams();
      found.path.replace(":deckId", deckId);
    }
    if (found.path.includes(":cardId")) {
      found.path.replace(":cardId", cardId);
      const { cardId } = useParams();
    }

    if (crumbs.indexOf(crumb) === crumbs.length - 1) {
      return <span>{crumb}</span>;
    } else {
      return <Link to={found.path}>{found.name}</Link>;
    }
  });

  return <div>{crumbArray}</div>;
};

export default Breadcrumb;
