import React, { useState } from "react";
import { useHistory, useParams } from "react-router-dom";
import { Button } from "./Button";
import { createCard } from "../utils/api/index";

// /decks/:deckId/cards/new

/** Create a new card and send it to the API
 *
 *  @param {array} decks
 *  the list of decks, {id, name, description}
 *  @param  {function} setLoading
 *  set true to trigger updating decks and a rerender
 */

function AddCard({ decks, setLoading }) {
  const { deckId } = useParams();
  const [addCardData, setAddCardData] = useState({});
  
  useEffect(() => {
    const abortController = new AbortController();
    async function loadAddCardData() {
      try {
        const currentDeck = await readDeck(deckId, abortController.signal);
        const { name, id } = currentDeck;
        const initialAddCardData = {
          deckId,
          front: "",
          back: "",
        };
        setAddCardData(initialAddCardData);
      } catch (error) {
        if (error.name === "AbortError") {
          console.log("loadCard")
        } else {
          throw error;
        }
      }
    }
    loadAddCardData();
    return () => abortController.abort;
  }, [input])

  const handleChange = ({ target }) => {
    setAddCardData({
      ...cardData,
      [target.name]: target.value,
    });
  };

  const history = useHistory();

  async function handleSubmit(event) {
    event.preventDefault();
    await createCard(id, cardData);
    setAddCardData(initialAddCardData);
    setLoading(true);
  }

  return (
    <div>
      <h2>Add Card</h2>
      <h3>Deck: {name}</h3>
      <form onSubmit={handleSubmit} className="form-group">
        <label htmlFor="frontText">Front</label>
        <textarea
          className="form-control"
          id="front"
          name="front"
          rows="3"
          onChange={handleChange}
          value={addCardData.front}
        />
        <label htmlFor="backText">Back</label>
        <textarea
          className="form-control"
          id="back"
          name="back"
          rows="3"
          onChange={handleChange}
          value={addCardData.back}
        />
        <Button
          onClick={() => {
            setLoading(true);
            return history.push(`/decks/${id}`);
          }}
        >
          Done
        </Button>
        <Button type="submit">Save</Button>
      </form>
    </div>
  );
}

export default AddCard;
