import React from "react";
import { Link } from "react-router-dom";

const Breadcrumb = ({ crumbs, currentDeck = null }) => {
  // don't render single crumb
  if (crumbs.length <= 1) {
    return null;
  }

  return (
    <div>
      {/* Link back to each previous step of breadcrumb */}
      {crumbs.map(({ name, path }, key) =>
        // last crumb shouldn't be a link
        key + 1 === crumbs.length ? (
          <span key={key}>{name}</span>
        ) : (
          <Link key={key} to={path}>
            {name}
          </Link>
        )
      )}
    </div>
  );
};

export default Breadcrumb;
