import React from "react";
import { Switch, useRouteMatch, Route } from "react-router-dom";
import Button from "./Button";
import EditDeck from "./EditDeck";

function Deck() {
  const { url } = useRouteMatch();
  return (
    <div>
      <Switch>
        <Route exact path="/decks/:userId">
          <h2>Deck Name</h2>
          <p>Here is where a sentence will go which is a description of the deck. Is that enough words?</p>
          <Button>Edit</Button>
          <Button>Study</Button>
          <Button>Add Cards</Button>
          <Button>{url}</Button>
          {/* TODO: map out all the cards here */}
          <h3>Cards</h3>
        </Route>
        <EditDeck />
      </Switch>
    </div>
  );
}

export default Deck;
