import React from "react";
import { Button, DeleteButton } from "../Button";
import { Link, useParams, useRouteMatch } from "react-router-dom";
import { deleteCard } from "../../utils/api/index";
function CardList({ cards }) {
  const { url } = useRouteMatch();
  const { cardId } = useParams();

  async function deleteHandler(id) {
    if (
      window.confirm("Delete this card?\n\nYou will not be able to recover it.")
    ) {
      await deleteCard(id);
      setLoading(true);
    }
  }
  
  if (cards) {
    const cardCards = cards.map((card) => (
      <div>
        <div className="card border-primary mb-3">
          <div className="card-body">
            <h4 className="card-text text-danger">Front</h4>
            <p className="card-text">{card.front}</p>
            <br />
            <h4 className="card-text text-danger">Back</h4>
            <p className="card-text">{card.back}</p>
            <Link to={`${url}/cards/${cardId}/edit`}>
              <Button>Edit</Button>
            </Link>
            <DeleteButton onClick={deleteHandler(card.id)} >Delete</DeleteButton>
          </div>
        </div>
        <br />
        <br />
      </div>
    ));
    return cardCards;
  } else {
    return "Loading...";
  }
}

export default CardList;
