import React from "react";
import { Switch, useRouteMatch, Route, Link } from "react-router-dom";
import AddCard from "./cards/AddCard";
import Button from "../Button";
import EditDeck from "./EditDeck";

// decks/:deckId

function Deck({decks}) {
  const { url } = useRouteMatch();
  const { id, name, description } = decks;
  return (
    <div>
      <Switch>
        <Route exact path="/decks/:userId">
          <h2>Deck Name</h2>
          <p>Here is where a sentence will go which is a description of the deck. Is that enough words?</p>
          <Link to={`${url}/edit`}><Button>Edit</Button></Link>
          <Link to={`${url}/study`}><Button>Study</Button></Link>
          <Link to={`${url}/cards/new`}><Button>Add Card</Button></Link>
          <Button>Delete</Button>
          {/* TODO: map out all the cards here */}
          <h3>Cards</h3>
        </Route>
        <Route path="/decks/:deckId/edit">
          <EditDeck />
        </Route>
        <Route>
          <AddCard />
        </Route>
      </Switch>
    </div>
  );
}

export default Deck;
