      .then(({id}) => history.push(`/decks/${id}`))
      .catch((console.log)); 
      
    
  }

  
  return (
    <div>
      <h2>{name}: Add Card</h2>
      <div className="form-group">
        <label htmlFor="frontText">Front</label>
        <textarea className="form-control" id="frontText" rows="3" />
      </div>
      <div className="form-group">
        <label htmlFor="backText">Back</label>
        <textarea className="form-control" id="backText" rows="3" />
      </div>
      <Button type="submit">Done</Button>
      <Button>Save</Button>
    </div>
  );
}

export default AddCard;
