import React, { useEffect, useState } from "react";
import {
  Switch,
  useRouteMatch,
  Route,
  useParams,
} from "react-router-dom";
import AddCard from "./cards/AddCard";
import EditDeck from "./EditDeck";
import Breadcrumb from "../Breadcrumb";
import EditCard from "./cards/EditCard";
import ViewDeck from "./ViewDeck";
import Study from "../Study/Study";

function Deck({ decks, setLoading, setError, ...restProps }) {
  const { url } = useRouteMatch();
  const { deckId } = useParams();
  const [currentDeck, setCurrentDeck] = useState({})

  useEffect(() => {
    const abortController = new AbortController();
    setLoading(true);

      const deckToSetCurrent = decks.find((deck) => deck.id === Number(deckId));
  
      Promise.resolve(deckToSetCurrent)
        .then((result) => setCurrentDeck(result))
        .catch(setError)
        .then(setLoading(false))

  }, [deckId, decks])

  console.log(currentDeck);

  return (
    <div>
      <Switch>
        <Route exact path="/decks/:deckId">
          <Breadcrumb crumbs={["Home", "Deck"]} />
          <ViewDeck currentDeck={currentDeck} />
        </Route>
        <Route path="/decks/:deckId/edit">
          <Breadcrumb crumbs={["Home", "Deck", "Add Card"]} />
          <EditDeck decks={decks} setLoading={setLoading} />
        </Route>
        <Route path="/decks/:deckId/cards/new">
          <Breadcrumb crumbs={["Home", "Deck", "Add Card"]} />
          <AddCard decks={decks} setLoading={setLoading} />
        </Route>
        <Route path="/decks/:deckId/cards/:cardId/edit">
          <Breadcrumb crumbs={["Home", "Deck", "Edit Deck"]} />
          <EditCard currentDeck={currentDeck} />
        </Route>
        <Route path="/decks/:deckId/study">
          <Breadcrumb crumbs={["Home", "Deck", "Study"]} />
          <Study currentDeck={currentDeck} />
        </Route>
      </Switch>
    </div>
  );
}

export default Deck;
