import React, { useState } from "react";
import Breadcrumb from "../Breadcrumb";
import Button from "../Button";

// /decks/new

function CreateDeck({decks, setDecks}) {
  // TODO: implement creating a deck
  const [formData, setFormData] = useState({});
  const [nextDeckId, setNextDeckId] = useState(0);

  function findHighestId(decks) {
    const idArray = decks.map(({id}) => id);
    return (Math.max(...idArray));
  }

  const initialNextDeckId = findHighestId(decks) + 1;

  setNextDeckId(initialNextDeckId);

  const handleChange = ({target}) => {
    setFormData({
      ...formData,
      [target.name]: target.value,
    });
    console.log(formData)
  };
  
  const handleSubmit = (event) => {
    event.preventDefault();
    setDecks([...decks, formData]);
    setFormData({})
  }

  return (
    <div>
      <Breadcrumb />
      <h2>Create Deck</h2>
      <form className="form-group">
        <label className="col-form-label" htmlFor="deckName">Name</label>
        <input 
          id="deckName"
          type="text"
          name="name"
          onChange={handleChange}
          className="form-control"
          value={formData.name}
          placeholder="Deck Name"
        />
        <label htmlFor="deckDescription">Description</label>
        <textarea
          id="deckDescription"
          name="description"
          onChange={handleChange}
          className="form-control"
          value={formData.description}
          
          rows="3"
          placeholder="Brief description of the deck"
        />
        <Button>Cancel</Button>
        <Button type="submit">Submit</Button>
      </form>
    </div>
  );
}

export default CreateDeck;
